makan_df['Size_in_sqft']=makan_df['Size_in_sqft'].apply(obj_to_int)

"""---------------------Convert Data(String) to Number---------------------"""

def month_year_to_days(data):
    days = 0
    data_list = data.split(' ')
    if data_list[0].lower()=='a' or data_list[0].lower()=='one' or data_list[0].lower()=='an':
        num = 1
    else:
        num = int(data_list[0])
    if data_list[1].lower()=='day' or data_list[1].lower()=='days':
        days = num * 1
    elif data_list[1].lower()=='month' or data_list[1].lower()=='months':
        days = num *30
    elif data_list[1].lower()=='year' or data_list[1].lower()=='years':
        days = num * 30 * 12
    return int(days)

makan_df['Posted_On'] = makan_df['Posted_On'].apply(month_year_to_days)
makan_df.rename(columns={'Posted_On':'Post_days'}, inplace=True)

